// import React from 'react';

import { useState } from "react";
import { Link } from "react-router-dom";

const AddRecipe = () =>{
  const [title, setTitle] = useState('')
  const [Kcal, setKcal] = useState('')
  const [prepare, settime] = useState('')
  const [desc, setdesc] = useState('')
  const handleSubmit = async (e) => {
    e.preventDefault()
    const addresep = {title,Kcal,prepare,desc}
    const response = await fetch('/api/user')
  }
    return (
        <div className="modal-overlay">
        <div className="modal-content-recipe">
          <Link className="close-button" to ='/Recipes'>X</Link>
          <h2>Create a Recipe</h2>
          <form className="addrecipe" onSubmit={handleSubmit}>
            <div className="form-input-container">
              <label>Recipe Name</label>
              <input 
                className="post-input-recipe" 
                type="text" 
                name="title" 
                required 
                placeholder=". . ."
                onChange={(e) => setTitle(e.target.value)}
                value={title}
              />
            </div>
            <div className="form-input-container">
              <label>Preparation Time</label>
              <input 
                className="post-input-recipe" 
                type="number" 
                name="title" 
                required 
                placeholder=". . . Mins" 
                onChange={(e) => settime(e.target.value)}
                value={prepare}
              />
            </div>
            <div className="form-input-container">
              <label>Calories</label>
              <input 
                className="post-input-recipe" 
                type="number" 
                name="title" 
                required 
                placeholder=". . . Kcal"
                onChange={(e) => setKcal(e.target.value)}
                value={Kcal}
              />
            </div>
            
            <div className="form-input-container">
              <label>Step</label>
              <textarea 
                className="post-input-recipe" 
                name="content" 
                required 
                placeholder="Share your recipe..." 
              ></textarea>
            </div>
            <button className="post-button" type="submit">Post</button>
          </form>
        </div>
      </div>
    );
}

export default AddRecipe
